!INC KG.TekstHelp

 /* OpenApi 3.0 Enumeration object
 * [verplicht] name: de naam van de enumratie
 * [optioneel] items: de EnumerationItems
 */
function Enumeration(name)
{
	this.name = name;
	this.items = new Collection();

	this.addItem = function(value, description)
	// voegt een enum waarde toe
	// geeft dat EnumerationItem terug 
	{
		item = new EnumerationItem(value, description);
		this.items.addItem(item);
		//VT(t, "enumeration.addItem", item.value + " ["+this.items.length+"]");
		return item;
	}

	this.aantalItems = function()
	{
		return this.items.length;
	}

	this.printAll = function (indentatie)
	// print de enumeratie
	{
		OI (indentatie, "enum:");
		for (var e=0; e < this.items.length; e++)
		{
			// print - enumeratiewaarde (indentatie diep)
			this.items.elements[e].printAll(indentatie + 1);
		}				
	}

	this.printDocumentation = function (indentatie)
	// print de documentatie van de hele enumeratie
	{
		// uitzonderingen even hard gecodeerd: Ja, JaNee
		if (this.name != "Ja" && this.name != "JaNee"){
			OI (indentatie, "De waarde is &#233;&#233; van de volgende:");
			for (var e=0; e < this.items.length; e++)
			{
				// print documentatie van enumeratiewaarde (indentatie diep)
				this.items.elements[e].printDocumentation(indentatie);
			}
		}		
	}	
}

function EnumerationItem(value, description)
{
	this.value = value;
	this.description = description;

	this.printAll = function (indentatie)
	// print de enumeratiewaarde
	{
		OI (indentatie, "- " + this.value);
	}

	this.printDocumentation = function (indentatie)
	// print de documentatie van de enumeratiewaarde
	{
		var waarde = this.value;
		if (this.description != null & this.description != "")
			{
				waarde += ": " + CamelCase(this.description);
			};
		OI (indentatie, "<li>" + waarde);
	}

}

