 */

 /* OpenApi 3.0 Schema object
  * name: naam
  * description: beschrijving
  * properties: collection Property objecten
  * parent: Reference object
 */
 /* TODO:
  * mogelijk omwerken naar Schema + Object (zie diagram JS);
  * het Schema is dat wat een naam heeft en mogelijk een Object als type kan hebben
  * maar ook gewoon een type: string kan zijn
  * wat dat betreft (we gebruiken het zelf niet in onze schema's vandaar dat ik het over het hoofd heb gezien)
  * klopt dit objectmodelletje voor de OpenApi3 specs nog niet helemaal
  * echter: voor nu van toepassing en okee
  */
function Schema(name, description)
{
	this.name = name;

	this.description = description;
	this.description = this.description.replace(/(\r\n\r\n\r\n)/gm,"\n");
	this.description = this.description.replace(/(\r\n\r\n)/gm,"\n");
	this.description = this.description.replace(/(\r\n)/gm,"\n        ");

	this.properties = new Collection();

	this.parent; //ApenApi3 Reference

	this.addProperty = function(property) 
       // adds a property to the schema
       // returns 0 when succesful.
       {
			return this.properties.addItem(property);
       }

	this.setParent = function (reference)
	   // set the reference to the parent schema
	   {
		   this.parent = reference;
	   }

	this.getNumberOfRequiredProperties = function() 
       // gets the number of required properties of this schema
       {
			var count = 0;		   
			// doorloop alle properties
			for (var i=0; i<this.properties.length; i++)
			{
				// tel als property required is
				if (this.properties.elements[i].required == true)
				{
					count++;
				}
			}
			return count;
       }


	this.printAll = function() 
	   // outputs gehele schema met indentatie 2
		{
			// schema is 2 diep
			OI(0, "\r\n");
			//OI(2, "# " + this.name + " representatie");
			OI(2, this.name + ":");
			OI(3, "description: >-");
			OI(4, this.description);

			//test
			VT(t, "this.properties.length", this.properties.length);
			VT(t, "this.getNumberOfRequiredProperties", this.getNumberOfRequiredProperties());

			if (this.getNumberOfRequiredProperties() > 0){
				OI(3, "required:");
				// alle required velden opsommen
				for (var i=0; i<this.properties.length; i++)
				{
					// print - propertynaam (4 diep)
					var property = this.properties.elements[i];
					if (property.required){
						OI(4, "- " + property.name);
					}
				}				
			}

			// TODO: print parent + - properties
			var parentOffset = 0;
			if (this.parent != null)
			{
				OI(3, "allOf:");
				OI(4, "- $ref: '" + this.parent.wholeReference() + "'");
				OI(4, "- properties:");
				parentOffset = 2;
			}
			else
			{
				// print properties kop (3 diep)
				OI(3, "properties:");
			}

			// doorloop alle properties en print ze (4 diep, maar 6 diep als er een parent is!)
			for (var i=0; i<this.properties.length; i++)
			{
				// print property
				var property = this.properties.elements[i];
				property.printAll(parentOffset);
			}


		}


}

