/* OpenApi 3.0 specification object
 * ATTRIBUTEN:
 * name: naam
 * mainVersion: de hoofdversie die in de filenaam verschijnt
 * version: het x.y.z. versienummer
 * title: de titel die in de title van de header verschijnt
 * description: idem description in de header
 * schemas: collection ApenAPI 3 Schema objecten
 * ---bestand---
 * filename: de naam van het bestand waarin de specificatie wordt weggeschreven
 * basepath: het absolute pad waar het bestand wordt weggeschreven
 * relativeFilepath: het relatieve pad waarin het bestand (eventueel) wordt weggeschreven
 * 
 * OPERATIES:
 * addSchema (schema): adds a schema to the specification
 * printAll: outputs gehele specificatie; naar session output en naar object 'bestand', als dat er is
 * TODO: nog netjes oplossen:  nu wordt 'bestand' in het begin (Geneer YAML script) gedeclareerd en overal gewoon zo gebruikt, dus niet doorgegeven...
 * Er kan er dus maar eentje tegelijk open zijn. Op zich geen probleem, zolang je er maar voor zorgt dat er 1 yaml tegelijk wordt geschreven.
 */
function OpenApiSpecification(name, mainVersion, version, title, description)
{
	this.name = name;
	this.mainVersion = mainVersion;
	this.version = version;
	this.title = title;
	this.description = description;
	this.description = this.description.replace(/(\r\n\r\n\r\n)/gm,"\n");
	this.description = this.description.replace(/(\r\n\r\n)/gm,"\n");
	this.description = this.description.replace(/(\r\n)/gm,"\n     ");
	this.schemas = new Collection();
	this.filename;
	this.basepath;
	this.relativeFilepath = "/";

	this.addSchema = function(schema) 
       // adds a schema to the spec
       // returns 0 when succesful.
       {
            return this.schemas.addItem(schema);
       }

	this.printAll = function()
	   // outputs gehele specificatie
		{
			OI(0, "openapi: 3.0.0");
			OI(0, "info:");
			OI(1,  "version: "+this.version);
			OI(1,  "title: >-");
			OI(2,  this.relativeFilepath + this.title + "_v" + this.mainVersion + ".yaml");
			OI(1, "description: >-");
			OI(2, this.description);
			OI(1, "contact:");
			OI(2, "name: GGM Team");
			OI(2, "email: ggm@kvk.nl");
			OI(0, "paths: {}");
			OI(0, "components:");
			OI(1, "schemas:");

			//test
			VT(t, "this.schemas.length", this.schemas.length);

			// doorloop alle schemas en print ze
			for (var i=0; i<this.schemas.length; i++)
			{
				// print schema
				var schema = this.schemas.elements[i];
				schema.printAll();
			}

		}

}

