// ***********************************************************************************
// Deze functie maakt van het aangegeven attribuut (een kolom in een databasetabel)
// een <<actueel gegeven kolom>> indien:
// de kolom:
//			- geen PK of FK kolom is
//			- maar wel eindigt op id
//			- en als er niet al een stereotype aan de kolom gegeven is
// ***********************************************************************************
function MaakAGKolom (attribuut){
	//test = aan
	//var t = true;

	//definities
	var theAttribute as EA.Attribute;
	var theParent as EA.Element;

	theAttribute = attribuut;
	TT(t, " ");
	TT(t, "===KOLOM: " + theAttribute.Name + "===");
	//TT(t, "ends with: " + theAttribute.Name.slice(-2));

	theParent = Repository.GetElementByID(theAttribute.ParentID);

	//check of kolom een echt ID is
	var isEchtID; //as boolean;
	isEchtID = false;

	var index, len;
	var method as EA.Method;
	for (index = 0, len = theParent.Methods.Count; index < len; ++index) {
		method = theParent.Methods.GetAt(index);
		TT(t, " ");
		TT(t, "<<"+method.Stereotype+">> " + method.Name);
		TT(t, "parameter[0]: " + method.Parameters.GetAt(0).Name);

		//check of de kolom een PK of FK is
		if ( (method.Stereotype=="FK" || method.Stereotype == "PK") && method.Parameters.GetAt(0).Name == theAttribute.Name) {
			TT(t, " ");
			MT(t, "Deze kolom is een PK or FK!");
			isEchtID = true;
			break;
		}
	}

	TT(t, " ");
	TT(t, "eindigt op id = " + (theAttribute.Name.slice(-2) =="id"));
	TT(t, "isEchtID = " + isEchtID);
	TT(t, "huidig stereotype = " + theAttribute.Stereotype);

	//maak de kolom een <<actueel gegevens kolom>> als deze geen PK of FK kolom is maar wel eindigt op id
	//en als er niet al een stereotype aan de kolom gegeven is
	if (isEchtID == false && theAttribute.Name.slice(-2) =="id" && theAttribute.Stereotype == ""){
		theAttribute.Stereotype = "actueel gegeven kolom";
		theAttribute.Update();
		TT(t, " ");
		MT(t, "<<actueel gegeven kolom>> gemaakt");
	}
	else{
		TT(t, " ");
		MT(t, "geen <<actueel gegeven kolom>> gemaakt");
	}
}

