/*
 * Script Name: Collection
 * Author: Jurn en Mark
 * Purpose: Collection is een collectie klasse waaraan je items kan toevoegen en items uit kunt verwijderen
 * Date: 2019
 *
 * Creation:
 * 	  var mycoll = new Collection();
 *
 * Functions:
 *
 *   mycoll.addItem(element)                      // adds element to the tail of the list. returns the element.
 *   mycoll.delItem(index)                 // removes element from position index of the list.
 *                                            // mylist:   { a b E c d}
 *                                            // mylist.delItem(2);
 *                                            // mylist:   { a b c d}
 *   mycoll.member(element)                  // returns the index position of the element in the list.
 *                                            // mylist:   { a b E c d}
 *                                            // mylist.indexOf("E");
 *   getItems()								// returns an array with the collection.
 *
 *   printAll = function()					// outputs overview of the collection. 
 */


function Collection()
{
       this.elements = new Array();
       this.length = 0;                                      //function(){return this.elements.length;}
       this.addItem = function(item) 
       // adds the item to last position of the collection.
       // returns 0 when succesful.
       {
             this.elements[this.length]=item;
             this.length++;
             return 0;
       }
       this.member = function(item)
       // tests whether collections contains an item.
       // returns the first index of the item when the collection contains the item
       // -1 otherwise.
       {
             for (i=0; i<this.length; i++)
             {
                    if (item == this.elements[i]) {return i;}
             }
             return -1;
       }
       this.delItem = function(item)
       // deletes an item from a collection.
       // returns 0 when succesful
       // -1 otherwise.
       {
             delPosition = this.member(item);

             if ( delPosition >= 0)
             {
                    var newCollection = Array(this.length - 1);
                    var copyIndex = 0;
                    for (i=0; i<newCollection.length; i++)
                    {
                           if (item == this.elements[i]) {copyIndex++;}
                           newCollection[i] = this.elements[copyIndex];
                           copyIndex++;
                    }
             this.elements = newCollection;

             this.length--;
             return 0;
             }
             return -1;
       }
       this.getItems = function()
       // returns an array with the collection.
       {
             return this.elements;
       }	   
       this.printAll = function()
       // outputs overview of the collection. 
       // indexes always prints, elements only when in printable representation.
       {
             for (var i=0; i<this.elements.length; i++)
             {
                    Session.Output("Element = " + i + " Value = " + this.elements[i]);
             }
       }
}

