 */
function OnProjectBrowserScript()
{
	KT(t, "Stereotypeer alle kolommen in een NPA tabel (OnProjectBrowserScript)");
	// Get the type of element selected in the Project Browser
	var treeSelectedType = Repository.GetTreeSelectedItemType();


	// Handling Code: Uncomment any types you wish this script to support
	// NOTE: You can toggle comments on multiple lines that are currently
	// selected with [CTRL]+[SHIFT]+[C].
	switch ( treeSelectedType )
	{
		case otElement :
		{
			// haal de tabel op
			theElement = Repository.GetTreeSelectedObject();

			//stereotypeer deze en haar kolommen als nodig
			StereotypeerTabelEnKolommen(theElement);

			break;
		}

		// DOORLOOP ALLE ELEMENTEN IN EEN PACKAGE
		case otPackage :
		{
			// Code for when a package is selected
			var thePackage as EA.Package;
			thePackage = Repository.GetTreeSelectedObject();

			//thePackage.Elements


			break;
		}

		//		case otDiagram :
//		{
//			// Code for when a diagram is selected
//			var theDiagram as EA.Diagram;
//			theDiagram = Repository.GetTreeSelectedObject();
//			
//			break;
//		}
//		case otAttribute :
//		{
//			// Code for when an attribute is selected
//			var theAttribute as EA.Attribute;
//			theAttribute = Repository.GetTreeSelectedObject();
//			
//			break;
//		}
//		case otMethod :
//		{
//			// Code for when a method is selected
//			var theMethod as EA.Method;
//			theMethod = Repository.GetTreeSelectedObject();
//			
//			break;
//		}
		default:
		{
			// Error message
			//Session.Prompt( "This script does not support items of this type.", promptOK );
			Session.Output( "Op dit soort items kun je het script niet runnen.");
		}
	}
}

function StereotypeerTabelEnKolommen (element)
{
	// Code for when an element is selected
	// In dit geval gaat het om een tabel
	var theElement as EA.Element;
	theElement = element;

	// tabel-stereotypen object
	var tabelStereotypen = {
		formeel:false,
		materieel:false,
		toString: function() {
			var s ="";
			if (this.formeel){
				s = "formeel";
			}
			if (this.materieel){
				s = s + ",materieel";
			}
			//VT(t,"s",s);
			return s;
		}
	};

	//definieer het object kolomstereotypen gelijkvormig als tabelstereotypen
	var kolomStereotypen = tabelStereotypen;

	//tabel naam
	VT(t, "Tabel naam", theElement.Name);
	//tabel stereotypen
	VT(t, "Tabel stereotype", theElement.Stereotype);
	VT(t, "Tabel stereotypeEx", theElement.StereotypeEx);

	if (theElement.Stereotype != "table"){
		Session.Output("Dit script moet op een tabel worden gedraaid.");
		return;
	}

	// ******************************
	// DE REST DUS ALLEEN OP TABELLEN
	// ******************************


	//doorloop alle attributen
	var index, len;
	var att as EA.Attribute;
	for (index = 0, len = theElement.Attributes.Count; index < len; ++index) {
		//het huidige attribuut
		att = theElement.Attributes.GetAt(index);
		AT(t, "<<"+att.Stereotype+">> " + att.Name + " : " + att.Type);

		// Als er niet al een stereotype is op de kolom, roep dan de functie
		// StereotypeerAttribuut aan
		if (att.Stereotype == "" || att.Stereotype == "column"){
			kolomStereotypen = StereotypeerAttribuut (att);
		}

		//test kolomStereotypen
		VT(t && kolomStereotypen.formeel,"kolomStereotypen.formeel", kolomStereotypen.formeel);
		VT(t && kolomStereotypen.materieel,"kolomStereotypen.materieel", kolomStereotypen.materieel);
		//VT(t,"kolomStereotypen.toString()", kolomStereotypen.toString());

		// zet tabelStereotypen op basis van kolomstereotypen
		if (kolomStereotypen.formeel){
			tabelStereotypen.formeel = true;
		}
		if (kolomStereotypen.materieel){
			tabelStereotypen.materieel = true;
		}

		//test tabelStereotypen
		//VT(t && tabelStereotypen.formeel,"tabelStereotypen.formeel", tabelStereotypen.formeel);
		//VT(t && tabelStereotypen.materieel,"tabelStereotypen.materieel", tabelStereotypen.materieel);
		//VT(t,"tabelStereotypen.toString()", tabelStereotypen.toString());					

	}

	//test tabelStereotypen aan het eind
	//TT(t, "tabelStereotypen aan het eind");
	VT(t && tabelStereotypen.formeel,"tabelStereotypen.formeel", tabelStereotypen.formeel);
	VT(t && tabelStereotypen.materieel,"tabelStereotypen.materieel", tabelStereotypen.materieel);
	//VT(t,"tabelStereotypen.toString()", tabelStereotypen.toString());

	// Zet tabel stereotypen formeel en materieel indien er formele of materiele kolommen waren
	// tenzij dit al was gedaan
	if (tabelStereotypen.formeel && !tabelStereotypen.materieel && theElement.StereotypeEx.search("formeel")==-1){
		MT(t, "tabel " + theElement.Name + "  wordt <<formeel>>");
		theElement.Stereotype = "formeel";
		theElement.Update();
		//schrijf stereotype <<table>> extra, zodat deze altijd vooraan staat (en PKs en FKs getoond blijven)
		theElement.Stereotype = "table";
		theElement.Update();
	}
	if (tabelStereotypen.materieel && theElement.StereotypeEx.search("materieel")==-1){
		MT(t, "tabel " + theElement.Name + "  wordt <<materieel>>");
		theElement.Stereotype = "materieel";
		theElement.Update();
		//schrijf stereotype <<table>> extra, zodat deze altijd vooraan staat (en PKs en FKs getoond blijven)
		theElement.Stereotype = "table";
		theElement.Update();
	}

	//herhaling tabel stereotypen
	AT(t, "Tabel aan het eind");
	VT(t, "Tabel stereotype", theElement.Stereotype);
	VT(t, "Tabel stereotypeEx", theElement.StereotypeEx);

	return;

}

// STEREOTYPEER EEN KOLOM
function StereotypeerAttribuut (att){

	// stereotypen object
	var stereotypen = {
		formeel:false,
		materieel:false,
		toString: function() {
			var s ="";
			if (this.formeel){
				s = "formeel";
			}
			if (this.materieel){
				s = s + ",materieel";
			}
			//VT(t,"s",s);
			return s;
		}
	};

	//VT(t,"stereotypen.formeel", stereotypen.formeel);
	//VT(t,"stereotypen.materieel", stereotypen.materieel);


	//TT(t, "eindigt op id = " + (att.Name.slice(-2) =="id"));
	//TT(t, "eindigt op li = " + (att.Name.slice(-2) =="li"));

	VT(t, "type", att.Type); 

	switch ( true ){
		case (att.Type == "TIMESTAMP" && att.Name != "TRANS_START"):
		{
			MT(t, "formeel");
			att.Stereotype = "formeel";
			//update
			att.Update();

			//zet formeel op true
			//VT(t,"stereotypen.formeel", stereotypen.formeel);
			stereotypen.formeel = true;
			//VT(t,"stereotypen.formeel", stereotypen.formeel);

			break;
		}
		case (att.Name == "TRANS_START"):
		{
			MT(t, "npa hulp");
			att.Stereotype = "npa hulp";
			//update
			att.Update();

			break;
		}
		case (false): // case (att.Name == "aanvangdatum" || att.Name == "einddatum"):
		{
			MT(t, "materieel");
			att.Stereotype = "materieel";
			//update
			att.Update();

			//zet materieel op true
			stereotypen.materieel = true;
			//VT(t,"stereotypen.materieel", stereotypen.materieel);
			break;
		}
		case (false): // case (att.Name.slice(-2) =="li"):
		{
			MT(t, "hrd hulp");
			att.Stereotype = "hrd hulp";
			//update
			att.Update();
			break;
		}
		case (false): // case (att.Name == "domeinreferentie" || att.Name == "objectreferentie" || att.Name == "versie"):
		{
			MT(t, "hrd hulp");
			att.Stereotype = "hrd hulp";
			//update
			att.Update();
			break;
		}
		case (false): // case (att.Name == "heefteencorrectieondergaan"):
		{
			MT(t, "formeel");
			att.Stereotype = "formeel";
			//update
			att.Update();
			break;
		}

		default:
		{
			TT(t, "niets gedaan");			
		}

	}

	//test stereotypen
	//VT(t,"stereotypen.formeel", stereotypen.formeel);
	//VT(t,"stereotypen.materieel", stereotypen.materieel);
	//VT(t,"stereotypen.toString()", stereotypen.toString());

	//geef lijst stereotypen terug
	return stereotypen;

	//if then
	if (false) {
		if (att.Name.substring(0,11) == "gebeurtenis"){
			MT(t, "GEB!");
			}
			else
			{
				MT(t, "geen geb");
			}
		}
	}
OnProjectBrowserScript();

