/*
 * Project Browser Script main function
 */
function OnProjectBrowserScript()
{
	KT(t, "Stereotypeer alle kolommen in een HRD tabel (OnProjectBrowserScript)");
	// Get the type of element selected in the Project Browser
	var treeSelectedType = Repository.GetTreeSelectedItemType();


	// Handling Code: Uncomment any types you wish this script to support
	// NOTE: You can toggle comments on multiple lines that are currently
	// selected with [CTRL]+[SHIFT]+[C].
	switch ( treeSelectedType )
	{
		case otElement :
		{
			// Code for when an element is selected
			// In dit geval gaat het om een tabel
			var theElement as EA.Element;

			// haal de klasse op
			theElement = Repository.GetTreeSelectedObject();

			//klasse naam
			VT(t, "klasse naam", theElement.Name);
			//tabel stereotypen
			//VT(t, "klasse stereotype", theElement.Stereotype);
			//VT(t, "klasse stereotypeEx", theElement.StereotypeEx);

			//doorloop alle attributen
			var index, len;
			var att as EA.Attribute;
			for (index = 0, len = theElement.Attributes.Count; index < len; ++index) {
				//het huidige attribuut
				att = theElement.Attributes.GetAt(index);
				AT(t, "<<"+att.Stereotype+">> " + att.Name);
				VT(t, "att.Visibility", att.Visibility);
				VT(t, "att.LowerBound", att.LowerBound);
				VT(t, "att.UpperBound", att.UpperBound);

				//VT(t, "att.TaggedValues", att.TaggedValues);
				//VT(t, "att.TaggedValuesEx", att.TaggedValuesEx);

				//att.TaggedValues.Delete(0);

				att.Visibility = "Public";
				att.Update();

				VT(t, "att.Visibility", att.Visibility);

			}

			theElement.Update();

			break;
		}
//		case otPackage :
//		{
//			// Code for when a package is selected
//			var thePackage as EA.Package;
//			thePackage = Repository.GetTreeSelectedObject();
//			
//			break;
//		}
//		case otDiagram :
//		{
//			// Code for when a diagram is selected
//			var theDiagram as EA.Diagram;
//			theDiagram = Repository.GetTreeSelectedObject();
//			
//			break;
//		}
//		case otAttribute :
//		{
//			// Code for when an attribute is selected
//			var theAttribute as EA.Attribute;
//			theAttribute = Repository.GetTreeSelectedObject();
//			
//			break;
//		}
//		case otMethod :
//		{
//			// Code for when a method is selected
//			var theMethod as EA.Method;
//			theMethod = Repository.GetTreeSelectedObject();
//			
//			break;
//		}
		default:
		{
			// Error message
			//Session.Prompt( "This script does not support items of this type.", promptOK );
			Session.Output( "Op dit soort items kun je het script niet runnen.");
		}
	}
}


OnProjectBrowserScript();

