/*
 * Project Browser Script main function
 */
function OnProjectBrowserScript()
{
	// Get the type of element selected in the Project Browser
	var treeSelectedType = Repository.GetTreeSelectedItemType();

	// Handling Code: Uncomment any types you wish this script to support
	// NOTE: You can toggle comments on multiple lines that are currently
	// selected with [CTRL]+[SHIFT]+[C].
	switch ( treeSelectedType )
	{
		case otElement :
		{
			// Code for when an element is selected
			var theElement as EA.Element;
			theElement = Repository.GetTreeSelectedObject();

			MaakElementRepresentatie(theElement);

			break;
		}
		case otPackage :
		{
			// Code for when a package is selected
			var thePackage as EA.Package;
			thePackage = Repository.GetTreeSelectedObject();

			var index, len;
			var el as EA.Element;
			for (index = 0, len = thePackage.Elements.Count; index < len; ++index) {
				//het huidige element
				theElement = thePackage.Elements.GetAt(index);

				// maak <<representatie>> indien deze het nog niet is
				MaakElementRepresentatie(theElement);
			}

			break;
		}
//		case otDiagram :
//		{
//			// Code for when a diagram is selected
//			var theDiagram as EA.Diagram;
//			theDiagram = Repository.GetTreeSelectedObject();
//			
//			break;
//		}
//		case otAttribute :
//		{
//			// Code for when an attribute is selected
//			var theAttribute as EA.Attribute;
//			theAttribute = Repository.GetTreeSelectedObject();
//			
//			break;
//		}
//		case otMethod :
//		{
//			// Code for when a method is selected
//			var theMethod as EA.Method;
//			theMethod = Repository.GetTreeSelectedObject();
//			
//			break;
//		}
		default:
		{
			// Error message
			Session.Prompt( "This script does not support items of this type.", promptOK );
		}
	}
}

OnProjectBrowserScript();


function MaakElementRepresentatie(element)
{
	var el as EA.Element;
	el = element;

	TT(t, "--------");
	VT(t, "el.Type", el.Type);
	VT(t, "el.Name", el.Name);
	VT(t, "el.Stereotype", el.Stereotype);
	if (el.Type == "Class" && el.Stereotype != "representatie"){
		TT(t, "klasse, nog geen representatie");
		el.Stereotype = "representatie";
		el.Update();
		TT(t, "klasse "+el.Name+" is nu een <<representatie>>.");
	}
	if (el.Type == "Enumeration" && el.Stereotype != "representatie"){
		TT(t, "enumeratie, nog geen representatie");
		el.Stereotype = "representatie";
		el.Update();
		TT(t, "enumeratie "+el.Name+" is nu een <<representatie>>.");
	}
}

!INC KG.FileHelp

// OUTPUT TEKST
function O(tekst){
	Session.Output(tekst);

	if (bestand != null)
	{
		SchrijfNaarBestand (bestand, tekst);
	}
}

function OI(numberOfTabs, tekst){
	var tabs = "";
	var index;
	for (index = 0; index < numberOfTabs; ++index) {
		tabs += "  ";
	}
	Session.Output(tabs+tekst);
	if (bestand != null)
	{
		SchrijfNaarBestand (bestand, tabs+tekst);
	}
}

!INC Local Scripts.EAConstants-JScript
!INC KG.Collection
!INC YAML Lib.OpenApi3 Reference
!INC YAML Lib.OpenApi3 Enumeration
!INC KG.DebugHelp
!INC KG.SessionOutputHelp

//testen aan of uit
var t = false;

/*
 * Script Name: 
 * Author: Mark Westbroek
 * Purpose: 
 * Date: 
 */

 /* OpenApi 3.0 Property object
 * [verplicht] name: de naam van de property
 * [optioneel] required: (true of false) (indien afwezig: false)
 * [optioneel] isArray: true als de property een array is.
 * [optioneel] reference: referentie naar een andere schema via een Reference object.
 * Indien er geen reference object is, zijn onderstaande mogelijke van toepassing
 * [optioneel] description: de beschrijving van de property
 * [optioneel] type: het type (indien leeg: object)
 * [optioneel] format: het format (indien leeg: geen format)
 * indien geen format, kan de property beperkt worden door een lijst waarden (enumeratie)
 * [optioneel] enumeration: collection enumeratiewaarden (indien leeg: geen enum)
 * [optioneel[ foutmelding: er is een foutmelding gegenereerd bij het maken het object
 */


 /*
 TODO

 Array Length
You can define the minimum and maximum length of an array like so:

    type: array
    items:
      type: integer
    minItems: 1
    maxItems: 10

Without minItems, an empty array is considered valid. 

--> Cardinality object gebruiken?

*/

function Property(name)
{
	// constructor
	this.name = name;
	this.required = false;
	this.isArray = false;
	this.reference; //ApenApi3 Reference
	this.description;
	this.type;
	this.format; // = "Testformat";
	this.enumeration; // ApenApi3 Enumeration
	this.foutmelding;

	// niet gebruikt, tenzij er iets speciaals moet gebeuren
	this.setRequired = function(req)
	// sets required
    {
		this.required = req;
	}

	this.setDescription = function(description)
	// sets description and clears of too many white lines or line breaks at all.
	{
		this.description = description;
		this.description = this.description.replace(/(\r\n\r\n\r\n)/gm,"\n");
		this.description = this.description.replace(/(\r\n\r\n)/gm,"\n");
		this.description = this.description.replace(/(\r\n)/gm,"\n          ");
	}



	this.printAll = function(extraIndent)
	// outputs gehele property met indentatie 4 + extraIndent
	{
		var b = 4 + extraIndent; // basis indentatie
		OI(b, this.name + ":");

		// [optioneel] reference: referentie naar een andere schema. Reference object.
		if (this.reference != null)
		{
			if (this.isArray)
			{
				if (typeof(this.description) != "undefined" && this.description != "")
				{
					// NOTE: description is useless with a reference, unless it's an array
					// TODO: IS THIS TRUE?
					OI(b+1, "description: >-");
					OI(b+2, this.description);
				}
				OI(b+1, "type: array");
				OI(b+1, "items:");
				++b;
			}
			OI(b+1, "$ref: '" + this.reference.wholeReference() + "'");
		}
		else
		{
			/*		
			 * Indien er geen reference object is, zijn onderstaande mogelijke van toepassing
			 * [optioneel] description: de beschrijving van de property
			 * [optioneel] type: het type (indien leeg: object)
			 * [optioneel] format: het format (indien leeg: geen format)
			 * indien geen format, kan de property beperkt worden door een lijst waarden (enumeratie)
			 * [optioneel] enumeration: collection enumeratiewaarden (indien leeg: geen enum)
			 */				
			if (typeof(this.description) != "undefined" && this.description != "")
			{
				OI(b+1, "description: >-");
				OI(b+2, this.description);
				// als er een enumeratie is, print dan de documentatie over de enumeratie ook
				if (typeof(this.enumeration) != "undefined")
				{
					this.enumeration.printDocumentation(b+2);
				}
			}
			else
			{
				// er is geen beschrijving, maar wel een enumeratie
				// print dan wel de documentatie over de enumeratie
				if (typeof(this.enumeration) != "undefined")
				{
					OI(b+1, "description: >-");
					this.enumeration.printDocumentation(b+2);
				}
			}				

			if (this.isArray)
			{				
				OI(b+1, "type: array");
				OI(b+1, "items:");
				++b;
			}

			if (typeof(this.type) != "undefined")
			{
				OI(b+1, "type: " + this.type);
			}
			else
			{
				OI(b+1, "type: [UNDEFINED]"); //TODO: kan type leeg zijn?
			}

			if (typeof(this.format) != "undefined" && this.format != null && this.format !="")
			{
				OI(b+1, "format: " + this.format);
			}
			else if (typeof(this.enumeration) != "undefined")
			{
				this.enumeration.printAll(b+1);
			}
		}

		if (typeof(this.foutmelding) != "undefined")
		{
			OI(b+1, "# FOUTMELDING YAMLGENERATOR: " + this.foutmelding);
		}
	}
}

