function ZetAttribuuttypenOm(element)
{
	var el as EA.Element;
	el = element;

	TT(t, "--------");
	VT(t, "el.Type", el.Type);
	VT(t, "el.Name", el.Name);
	VT(t, "el.Stereotype", el.Stereotype);
	if (el.Type == "Class" && el.Stereotype == "representatie"){

		TT(t, "<<representatie>>");

		// doorloop attributen
		for (j = 0, nrOfAttributes = el.Attributes.Count; j < nrOfAttributes; ++j)
		// doorloop de attributen van het element
		// en check van elk het type
		// zet om indien nodig
		{
			var att as EA.Attribute;
			att = el.Attributes.GetAt(j);

			VT(t, "att.Name", att.Name);
			VT(t, "att.Type", att.Type);

			//vind [format]
			re = /\[.*?\]/g;

			var type = att.Type;
			var f = re.exec(type);
			VT(t, "[format]", f);

			if (f != null)
			{
				TT(t, "format not null");
			}
			else
			{
				/* toegestane YAML types zijn:
				* string (this includes dates and files)
				* number
				* integer
				* boolean
				* array
				* object

				* type 		format 	Description
				*number 		 	    Any numbers.
				*number 		float 	Floating-point numbers.
				*number 		double 	Floating-point numbers with double precision.
				*integer 	 		Integer numbers.
				*integer 	int32 	Signed 32-bit integers (commonly used integer type).
				*integer 	int64 	Signed 64-bit integers (long type).
				*
				*String Formats
				*An optional format modifier serves as a hint at the contents and format of the string. OpenAPI defines the following built-in string formats:
				*
				*    date  full-date notation as defined by RFC 3339, section 5.6, for example, 2017-07-21
				*    date-time  the date-time notation as defined by RFC 3339, section 5.6, for example, 2017-07-21T17:32:28Z
				*    password  a hint to UIs to mask the input
				*    byte  base64-encoded characters, for example, U3dhZ2dlciByb2Nrcw==
				*    binary  binary data, used to describe files (see Files below)
				*
				*However, format is an open value, so you can use any formats, even not those defined by the OpenAPI Specification, such as:
				*
				*    email
				*    uuid
				* 	 enz.
				*/

				if (type=="string" || type=="number" || type=="integer" || type=="boolean"){
					VT(t,"bekend type", type);
				}
				else if (type=="int")
				{
					att.Type = "integer";
					att.Update();
				}
				else
				{
					VT(t,"onbekend type", type);
					TT(t, "maak []");
					att.Type = "string [" + type + "]";
					var tv as EA.TaggedValue;
					tv = att.TaggedValues.AddNew("YAML::format", type);
					tv.Update();
					att.Update();				
				}
			}


		}	


	}
}

