/*
 * Maak bestand <naam> op het filesysteem en geeft dit object terug (ActiveXObject)
 * Nu: in default pad: N:\Projecten\Kern Gezond Projecten\10 GGM\60 Werkmap\Onderdeel - Modellen\[YAML]\[GENEREREN]\
 */

 /* TODO:
  * een Bestand object maken met de schrijf() en sluit() operaties
  */
function MaakBestand (padEnNaam){
	var fso = new ActiveXObject("Scripting.FileSystemObject");
	file = fso.CreateTextFile(padEnNaam, true);

	TT (t, "##### Bestand " + padEnNaam + " gemaakt. #####");
	return file;
}

function SchrijfNaarBestand( bestand, tekst)
{
	bestand.WriteLine(tekst);
}


function SluitBestand( bestand )
{
	bestand.Close();
}

