/*
 * Script Name: 
 * Author: 
 * Purpose: 
 * Date: 
 */

function AttributeTagCollection( attributeTagCollection /* : EA.Collection containing EA.AttributeTags */ )
{
	this.attributeTagCollection = attributeTagCollection;

	/* Finds an EA.AttributeTag in an EA.Collection containing EA.AttributeTags based on the name of the tag.
	 * returns an EA.AttributeTag
	 */
	this.FindAttributeTagByName = function ( name /* : string */ ) /* : EA.AttributeTag */
	{
		var atc as EA.Collection;

		//VT(t, "typeof attributeTagCollection", typeof(attributeTagCollection));
		atc = this.attributeTagCollection;

		if (atc == null)
		{
			return null;
		}
		else
		{
			// Find AttributeTag by name. The GetByName(name) in the EA Collection does not work properly with AttributeTags
			// Returns an "index out of bound" error
			// So we had to write this outselves.

			//var tagToBeFound as EA.AttributeTag;
			//tagToBeFound = null;

			for( var i = 0; i < atc.Count; i++ )
			{
				if (atc.GetAt(i).Name == name)
				{
					//tagToBeFound = atc.GetAt(i);
					//return tagToBeFound;
					return atc.GetAt(i);
					break;
				}
			}
		}
	}

	// TODO
	this.FindAttributeTagByValue = function( value /* string */ )
	{
	}	
}

