var t = true;



/*
 * Script Name: 
 * Author: Mark Westbroek
 * Purpose: De objecten rondom het maken en bewaren van Enumerations en hun links naar de attributen die worden beperkt door een enum.
 * Date: 
 */


// OLD ONE
function Enumerations()
{
 	this.enumerations = new Collection();

	this.addItem = function(enumeration)
	// voegt een enumeration toe
	// check wel of deze enumeration niet al in de lijst zit (op naam)!
	// niet in de lijst: voegt hem toe en geeft 0 terug
	// al wel in de lijst (op naam): voegt hem niet meer toe en geeft -1 terug
	{
		VT(t, "enumerations.addItem ["+this.enumerations.length+"]", enumeration.name);

		VT(t, "!!!!!!!!!!!!!!!getbyname", typeof(this.getByName(enumeration.name)) );

		if (typeof( this.getByName(enumeration.name) ) == "undefined")
		{
			// de enumeratie zit nog niet in de lijst
			return this.enumerations.addItem(enumeration);
		}
		else
		{
			return -1;
		}
	}


	this.getByName = function (name) /*: Enumeration of null */
	// zoekt de Enumeration bij naam
	{
		for (var e=0; e < this.enumerations.length; e++)
		{
			// return als item.name = name
			if (this.enumerations.elements[e].name == name)
			{
				VT(t, "Enumerations.getByName element ["+e+"] gevonden", this.enumerations.elements[e].name);
				return this.enumerations.elements[e];
			}
		}
	}

}


/* **************************************** NEW ONES ***************************************** */

function EnumerationRepresentations() {

	this.enumerationRepresentations = new Collection();
	this.linkingAttributes = new Collection();

	/// adds an EnumerationRepresentation
	/// does not add the EnumerationRepresentation if it already exists
	/// <param name="connector">(optional) The connector that connects to the enumeration representation; the clientEnd must be a representation element</param>
	/// <param name="enumRep">The enumeration representation</param>
	this.addEnum = function(connector /*EA.Connector*/, enumRep /*EA.Element*/) /*:EnumerationRepresentation*/
	{
		// check of de enumRep er al is (via het ElementGUID)
		var enRep;
		enRep = this.getByGUID (enumRep.ElementGUID);
		if ( enRep != null )
		{
			// EnumerationRepresentation is er al
			// voeg die dus niet meer toe aan de collectie
			TT (t, "### EnumerationRepresentations.addEnum ## EnumerationRepresentation " + enumRep.Name + " is er al ##");
		}
		else
		{
			// voeg EnumerationRepresentation toe aan de collectie
			enRep = new EnumerationRepresentation (enumRep);
			this.enumerationRepresentations.addItem( enRep );
		}

		// als er een connector vanuit een linkingRep is: zoek het attribuut of de attributen in de linkedRep op en voeg die toe aan de linkingAttributes lijst
		var con as EA.Connector;
		con = connector;
		if ( con == null )
		{
			// er is geen connector die linkt aan de enum, dus de functie is klaar
			// deze optie wordt in principe niet gebruikt
			TT(t,"### EnumerationRepresentations.addEnum ## er is geen linking connector");
			return enRep;
		}
		else
		{
			// er is een connector
			TT(t,"### EnumerationRepresentations.addEnum ## er is een linking connector");
			/*
			VT(t, "con.Name", con.Name);
			VT(t, "con.Type", con.Type);
			VT(t, "con.Direction", con.Direction);
			VT(t, "con.Name", con.Name);
			VT(t, "con.Notes", con.Notes);
			VT(t, "con.ClientID", con.ClientID);
			VT(t, "con.ClientEnd.Role", con.ClientEnd.Role);
			VT(t, "ClientID).Name", Repository.GetElementByID(con.ClientID).Name);
			VT(t, "con SupplierID", con.SupplierID);
			VT(t, "con.SupplierEnd.Role", con.SupplierEnd.Role);
			VT(t, "SupplierID).Name", Repository.GetElementByID(con.SupplierID).Name);
			VT(t, "con StyleEx", con.StyleEx);
			*/

			// vind de linkingRep
			var linkingRep as EA.Element;
			linkingRep = Repository.GetElementByID(con.ClientID);

			// is er maar 1 attribuut in de linkingRep?
			if ( linkingRep.Attributes.Count == 1 )
			{
				// dan link deze aan de enum
				TT(t,"### EnumerationRepresentations.addEnum ## the linking element has only one attribute: " + linkingRep.Attributes.GetAt(0).Name);
				this.linkingAttributes.addItem ( new LinkingAttribute( linkingRep.Attributes.GetAt(0), enRep) );
				return enRep;
			}
			else
			{
				TT(t,"### EnumerationRepresentations.addEnum ## the linking element has " + linkingRep.Attributes.Count + " attributes");

				/* er zijn meer attributen in het linkinRep element
				Twee situaties:
				1. de connector is gelinkt aan een attribuut:
					- doorloop de attributen en kijk welk attribuut er gelinkt is aan de enumRep
				2. de connector zit los:
					- doorloop de attributen en kijk of er soms maar 1 string attribuut is. Dan mag die beschouwd worden als het attribuut met de enumeratie.
					- bijv. bij een representatie met een "integer", een "string [DatumIncompleet]", een "string [Tijdstip]" en een gewone "string" mogen we aannemen dat de gewone string aan de enum hangt.
				*/
				/*
				ad 1.
					Hoe te vinden of het attribuut gelinkt is aan de connector:
					- de LFSP waarde in de StyleEx property van de connector bevat de GUID van het attribuut
					bijvoorbeeld con.StyleEx: LFSP={DA05F945-6DA5-4067-A85D-CA06C8B9F5BD}L;
					attribuut.AttributeGUID = {DA05F945-6DA5-4067-A85D-CA06C8B9F5BD}
					-> dit is het attribuut waar de dependency aan gelinkt is
				*/

				if (con.StyleEx != "")
				{
					TT(t,"### EnumerationRepresentations.addEnum ## the connector is probably linked to an attribute");
					/*
					1. de connector is gelinkt aan een attribuut:
					- doorloop de attributen en kijk welk attribuut er gelinkt is aan de enumRep
					*/

					// destill the linking attribute's GUID from the connector's StyleEx property
					re = /LFSP={([A-Fa-f0-9-]*?)}/g;
					var linkingAttGuid = "{" + re.exec(con.StyleEx)[1] + "}";
					//VT(t, "linkingAttGuid", linkingAttGuid);

					for (var k = 0, nrOfItems = linkingRep.Attributes.Count; k < nrOfItems; ++k)
					{
						var att as EA.Attribute;
						att = linkingRep.Attributes.GetAt(k);

						//VT(t, "linkingRep att.Name/GUID", att.Name + " " + att.AttributeGUID);					
						if (linkingAttGuid == att.AttributeGUID)
						{
							// dit is het attribuut dat linkt
							TT(t,"### EnumerationRepresentations.addEnum ## the linking element's linking attribute is " + linkingRep.Attributes.GetAt(k).Name);
							this.linkingAttributes.addItem ( new LinkingAttribute( linkingRep.Attributes.GetAt(k), enRep) );
							return enRep;
						}					
					}
					// het attribuut is niet gevonden!
					TT(t,"### EnumerationRepresentations.addEnum ## the linking element's linking attribute has not been found! ##");
				}

				// ga naar plan 2....

				/*
				2. de connector zit los:
				- doorloop de attributen en kijk of er soms maar 1 string attribuut is. Dan mag die beschouwd worden als het attribuut met de enumeratie.
				- bijv. bij een representatie met een "integer", een "string [DatumIncompleet]", een "string [Tijdstip]" en een gewone "string" mogen we aannemen dat de gewone string aan de enum hangt.
				*/
				TT(t,"### EnumerationRepresentations.addEnum ## the connector is not linked to an attribute. Try and find the only pure-string-attribute");
				// count the number of pure string attributes; if that number is 1, use this attribute
				var nrOfPureStringAttributes, lastPureStringIndex;
				nrOfPureStringAttributes = 0;
				lastPureStringIndex = -1;
				for (var k = 0, nrOfItems = linkingRep.Attributes.Count; k < nrOfItems; ++k)
				{
					var att as EA.Attribute;
					att = linkingRep.Attributes.GetAt(k);

					//VT(t, "linkingRep att.Type", att.Type);					
					if (att.Type == "string" || att.Type == "String")
					{
						lastPureStringIndex = k;
						++nrOfPureStringAttributes;
					}
				}
				if (nrOfPureStringAttributes == 1)
				{
					TT(t,"### EnumerationRepresentations.addEnum ## the only linking element's pure-string attribute is " + linkingRep.Attributes.GetAt(lastPureStringIndex).Name);
					this.linkingAttributes.addItem ( new LinkingAttribute( linkingRep.Attributes.GetAt(lastPureStringIndex), enRep) );
					return enRep;
				}
				else
				{
					TT(t,"### EnumerationRepresentations.addEnum ## there are " + nrOfPureStringAttributes + " linking element's pure-string attributes");
					TT(t,"### EnumerationRepresentations.addEnum ## cannot link the enum to an attribute!");
					return enRep
				}

			}
		}

	};


	/// <param name="name">The name of the enumeration to get</param>
	this.getByName = function(name /*string*/) /*: EnumerationRepresentation */
	{
		for (var g = 0, nrOfItems = this.enumerationRepresentations.elements.length; g < nrOfItems; ++g)
		{
			//VT(t, "EnumerationRepresentations[", g + "]");
			//VT(t, "Name", this.enumerationRepresentations.elements[g].Name);
			//VT(t, "GUID", this.enumerationRepresentations.elements[g].GUID);

			if ( this.enumerationRepresentations.elements[g].Name == name)
			{
				return this.enumerationRepresentations.elements[g];
			}			
		}	
		return null;
	};

	/// Gets an enumerationRepresentation by the GUID of the EA.Element
	/// If not found, returns null
	/// <param name="guid">The guid of the enumeration-representation EA.element</param>
	this.getByGUID = function(guid /*ElementGUID*/){

		//TT(t,"### EnumerationRepresentations.getByGUID( " + guid + " )");
		//VT(t,"this.enumerationRepresentations.elements.length", this.enumerationRepresentations.elements.length);
		for (var g = 0, nrOfItems = this.enumerationRepresentations.elements.length; g < nrOfItems; ++g)
		{
			//VT(t, "EnumerationRepresentations[", g + "]");
			//VT(t, "Name", this.enumerationRepresentations.elements[g].Name);
			//VT(t, "GUID", this.enumerationRepresentations.elements[g].GUID);

			if ( this.enumerationRepresentations.elements[g].GUID == guid)
			{
				return this.enumerationRepresentations.elements[g];
			}			
		}	
		return null;
	};

	/// Gets the EnumerationRepresentation by the GUID of the linking attribute
	/// To be used when generating Properties from attributes, in order to know if the property is confined by an enumeration.
	/// <param name="guid">The GUID of the linking attribute</param>
	this.getByLinkingAttributeGUID = function(guid /* string (AttributeGUID)*/) /*:EnumerationRepresentation*/
	{
		for (var g = 0, nrOfItems = this.linkingAttributes.elements.length; g < nrOfItems; ++g)
		{
			// VT(t, g, this.linkingAttributes.elements[g].Attribute.Name + " " + this.linkingAttributes.elements[g].Attribute.AttributeGUID + " -> " + this.linkingAttributes.elements[g].EnumerationRepresentation.Name);
			if ( this.linkingAttributes.elements[g].Attribute.AttributeGUID == guid)
			{
				return this.linkingAttributes.elements[g].EnumerationRepresentation;
			}
		}	
		return null;		
	};


	this.test = function ()
	{
		VT(t,"this.enumerationRepresentations.elements.length", this.enumerationRepresentations.elements.length);
		for (var g = 0, nrOfItems = this.enumerationRepresentations.elements.length; g < nrOfItems; ++g)
		{
			VT(t, g, this.enumerationRepresentations.elements[g].Name + " " + this.enumerationRepresentations.elements[g].GUID);
		}

		TT(t," ");
		VT(t,"this.linkingAttributes.elements.length", this.linkingAttributes.elements.length);
		for (var g = 0, nrOfItems = this.linkingAttributes.elements.length; g < nrOfItems; ++g)
		{
			var att as EA.Attribute;
			att = this.linkingAttributes.elements[g].Attribute;
			var attEl as EA.Element;
			attEl = Repository.GetElementByID(att.ParentID);

			VT(t, g, attEl.Name + " . " + att.Name + " -> " + this.linkingAttributes.elements[g].EnumerationRepresentation.Name);
		}

	}


}//end EnumerationRepresentations




/// <summary>
/// Deze functie maakt ook de Enumeration aan.
/// </summary>
/// <param name="enumRep">enumeration represention (an EA.Element)</param>
function EnumerationRepresentation(enumRep /*EA.Element*/) {
	//var el as EA.Element;
	//el.Name
	//el.ElementGUID

	this.EAElement = enumRep;
	this.Name = enumRep.Name;
	this.GUID = enumRep.ElementGUID;

	//create Enumeration from element
	TT(t, "### EnumerationRepresentation (" + this.Name +")");
	//VT(t, "GUID", this.GUID);

	this.Enumeration = new Enumeration(this.Name);

	// doorloop de attributen van het element
	// en voeg aan enumeratie toe
	for (var k = 0, nrOfItems = enumRep.Attributes.Count; k < nrOfItems; ++k)
	{
		var att as EA.Attribute;
		att = enumRep.Attributes.GetAt(k);

		//VT(t, "enum.Name", att.Name);
		//VT(t, "enum.Notes", att.Notes);
		//VT(t, "enum.Type", att.Type);
		var item = this.Enumeration.addItem(att.Name, att.Notes);
	}

	// test
	if (t)
	{
		//this.Enumeration.printAll(5);
		//this.Enumeration.printDocumentation(5);
	};



}//end EnumerationRepresentation




/// 
/// <param name="attribute">Het EA.Attribute dat linkt naar een enumeratie</param>
/// <param name="enumRep">De EnumerationRepresentation van de enumeratie</param>
function LinkingAttribute(attribute /*EA.Attribute*/, enumRep /*EnumerationRepresentation*/)
{
	this.Attribute = attribute;
	this.EnumerationRepresentation = enumRep;
}//end LinkingAttribute

