/*
 * Script Name: 
 * Author: Mark Westbroek
 * Purpose: 
 * Date: 
 */

 /* OpenApi 3.0 Reference object
 * [mag leeg zijn] specificationName: de naam van de openapi specification waarin dit schema gespecifceerd is (komt overeen met het EA package alias!)
 * [verplicht] specificationMainVersion: de naam van het schema waarnaar deze referentie refereert
 * [verplicht] schemaName: de naam van het schema waarnaar deze referentie refereert
 * [optioneel] relativeFilepath: het (relatieve) pad en naam van de file waarin het schema staat
 */
function Reference(specificationName, specificationMainVersion, schemaName, relativeFilepath)
{
	this.specificationName = specificationName;
	this.specificationMainVersion = specificationMainVersion;
	this.schemaName = schemaName;
	this.relativeFilepath = relativeFilepath;

	this.wholeReference = function ()
	// geeft de hele referentie
	{
		var whole = "";
		if (this.specificationName != null && this.specificationName != "")
		{
			if (this.relativeFilepath != null && this.relativeFilepath != "")
			{
				whole = this.relativeFilepath.replace(/\\/gi,"/");
			}
			whole += this.specificationName + "_v" + specificationMainVersion + ".yaml";
		}
		whole += "#/components/schemas/" + schemaName;
		return whole;
	}

}

