//testen aan of uit
var t = true;

// vanuit alle subscripts toegankelijk bestand (de file waar alles heen gaat)
var bestand;
var pad = "N:\\Projecten\\Kern Gezond Projecten\\10 GGM\\60 Werkmap\\Onderdeel - Modellen\\[YAML]\\[GENEREREN]\\";

/*
 * This code has been included from the default Project Browser template.
 * If you wish to modify this template, it is located in the Config\Script Templates
 * directory of your EA install path.   
 * 
 * Script Name:
 * Author:
 * Purpose:
 * Date:
 */

/*
 * Project Browser Script main function
 */
function OnProjectBrowserScript()
{
	// Get the type of element selected in the Project Browser
	var treeSelectedType = Repository.GetTreeSelectedItemType();

	// Handling Code: Uncomment any types you wish this script to support
	// NOTE: You can toggle comments on multiple lines that are currently
	// selected with [CTRL]+[SHIFT]+[C].
	switch ( treeSelectedType )
	{
		case otElement :
		{
			// Code for when an element is selected
			var theElement as EA.Element;
			theElement = Repository.GetTreeSelectedObject();

			//enums zijn leeg
			var enumeraties = new Enumerations(); // OUD
			var enumReps = new EnumerationRepresentations(); // NIEUW

			//maak yaml voor 1 element
			var yaml = new OpenApiSpecification("losElement_"+theElement.Name, theElement.Phase, theElement.Version, "losElement_"+theElement.Name, theElement.Notes);

			var schema = MaakSchemaUitElement (yaml, theElement, enumeraties, enumReps);
			schema.printAll();

			break;
		}

		case otPackage :
		{
			// Code for when a package is selected
			var thePackage as EA.Package;
			thePackage = Repository.GetTreeSelectedObject();

			// Package
			VT(t, "Package.Name", thePackage.Name);
			VT(t, "Package.Version", thePackage.Version);
			VT(t, "Package.Element.Phase", thePackage.Element.Phase);
			VT(t, "Package.Element.Genfile", thePackage.Element.Genfile);
			// maak een nieuw OpenApi(3) specificatie object.
			var yaml = new OpenApiSpecification(thePackage.Alias, thePackage.Element.Phase, thePackage.Version, thePackage.Alias, thePackage.Notes);

			yaml.basepath = pad;

			/* TODO: filename omzetten naar relativeFilepath en filename
			 *dus:
			 * representaties/opvragen/testspul2_v1.yaml
			 * ->
			 * representaties/opvragen/   en testspul2_v1.yaml
			 * ->
			 * representaties\\opvragen\\
			 *
			 * Maar dit lukt niet dus:
			 * N.B.
			 * filename is nu alleen het relatieve pad...
			 */

			//re = /[/\\]?([a-z0-9-_]+[/\\])*([a-z0-9-_]+\.yaml)/g;
			//var paddelen = re.exec(thePackage.Element.Genfile);

			//if (paddelen != null){		
			//	for (i = 0, nrOf = paddelen.length; i < nrOf; ++i)
			//	{
			//		VT(t, i, paddelen[i]);
			//	}
			//}


			// filename

			// ====tijdelijk even zo===
			yaml.filename = thePackage.Alias + "_v" + thePackage.Element.Phase + ".yaml";
			yaml.relativeFilepath = thePackage.Element.Genfile.replace( /\//gi, "\\");

			if (yaml.relativeFilepath.substr(yaml.relativeFilepath.length - 1) != "\\")
			{
				yaml.relativeFilepath = yaml.relativeFilepath + "\\";
			}

			VT(t, "yaml.basepath", yaml.basepath);
			VT(t, "yaml.relativeFilepath", yaml.relativeFilepath);
			VT(t, "yaml.filename", yaml.filename);


			//test
			if(t)
			{
				yaml.printAll();
			}

			// zoek alle enumeraties in deze package
			var enumeraties = new Enumerations(); // OUD
			var enumReps = new EnumerationRepresentations(); // NIEUW

			var index, len;
			var el as EA.Element;
			for (i = 0, nrOfElements = thePackage.Elements.Count; i < nrOfElements; ++i)
			{
				//het huidige element
				el = thePackage.Elements.GetAt(i);

				if (el.Stereotype == "representatie")
				{
					if (el.Type == "Enumeration")
					// als enum, voeg toe aan verzameling enums van mijn package object
					{
						// voeg element als een (met items gevulde) Enumeratie toe aan enumeraties collectie
						/// OUD /// enumeraties.addItem( MaakEnumeratieVanElement(el) );
						/// NIET NODIG /// enumReps.addEnum ( null, el);
					}
					else
					{
						TT(t, " ");
						TT(t, "#################################################################################################");
						VT(t, "###### Genereer YAML ### kijk of er vanuit dit element enums worden aangewezen", el.Name);
						// TODO
						// doorloop alle relaties van dit element
						// als dependency: is het uiteinde een enum?
						// zit deze al in de enumeraties lijst?
						// nee: voeg toe
						for (j = 0, nrOfConnectors = el.Connectors.Count; j < nrOfConnectors; ++j)
						// doorloop de Connectors van het element
						{
							var con as EA.Connector;
							con = el.Connectors.GetAt(j);

							/*
							TT(t,"**Connectors**");
							VT(t, "con.Name", con.Name);
							VT(t, "con.Type", con.Type);
							VT(t, "con.Direction", con.Direction);
							VT(t, "con.Name", con.Name);
							VT(t, "con.Notes", con.Notes);
							VT(t, "con.ClientID", con.ClientID);
							VT(t, "con.ClientEnd.Role", con.ClientEnd.Role);
							VT(t, "ClientID).Name", Repository.GetElementByID(con.ClientID).Name);
							VT(t, "con SupplierID", con.SupplierID);
							VT(t, "con.SupplierEnd.Role", con.SupplierEnd.Role);
							VT(t, "SupplierID).Name", Repository.GetElementByID(con.SupplierID).Name);
							VT(t, "con StyleEx", con.StyleEx);
							*/

							if (con.Type == 'Dependency' && con.ClientID == el.ElementID)
							{
								var doelEl as EA.Element;
								doelEl = Repository.GetElementByID(con.SupplierID);
								TT(t, " ");
								VT(t, "### Genereer YAML ### Dependency: ", "---" + con.Name + "---> " + "<<"+doelEl.Stereotype+">> " + doelEl.Name + "(type: " + doelEl.Type +")");

								if (doelEl.Type == "Enumeration" && doelEl.Stereotype == "representatie")
								{
									// voeg doel-element als een (met items gevulde) Enumeratie toe aan enumeraties collectie
									// N.B. deze operatie checkt al of de enum niet al in de lijst zit
									// maar ik doe het hier vooraf ook om niet helemaal een Enumeration te maken om die daarna niet toe te voegen
									if (typeof( enumeraties.getByName(doelEl.Name) ) == "undefined")
									{
										///OUD///enumeraties.addItem( MaakEnumeratieVanElement(doelEl) );
										enumReps.addEnum ( con, doelEl);
									}
								}								
							}
						}
						TT(t, "#################################################################################################");
					}
				}
			}

			TT(t, " ");
			TT(t, "#################################################################################################");
			TT(t, "###### Genereer YAML ### EnumerationRepresentations en LinkingAttributes");
			enumReps.test();
			TT(t, "#################################################################################################");


			TT(t, " ");
			TT(t, " ");
			TT(t, "******************************************************************************************************");
			VT(t, "****** Genereer YAML ****** zoek alle representaties in deze package: ", thePackage.Name + "******");
			TT(t, "******************************************************************************************************");

			// zoek alle representaties in deze package
			// maak er een schema van
			// en voeg toe aan YAML object
			var index, len;
			var el as EA.Element;
			for (var elementi = 0, nrOfElements = thePackage.Elements.Count; elementi < nrOfElements; ++elementi)
			{
				//het huidige element
				el = thePackage.Elements.GetAt(elementi);

				var schema = MaakSchemaUitElement (yaml, el, enumeraties, enumReps);

				if (typeof(schema) != "undefined")
					{
						// voeg schema toe aan yaml
						yaml.addSchema (schema);
					}
			}


			TT(t, "****************************************");
			TT(t, "********* HELE YAML HIERONDER***********");
			TT(t, "****************************************");
			// Maak het bestand waar alle tekst ook heengeschreven wordt
			VT(t, "yaml.basepath", yaml.basepath);
			VT(t, "yaml.relativeFilepath", yaml.relativeFilepath);
			VT(t, "yaml.filename", yaml.filename);

			bestand = MaakBestand (yaml.basepath + yaml.relativeFilepath + yaml.filename);

			yaml.printAll();

			// Sluit het bestand
			SluitBestand(bestand);

			break;
		}
//		case otDiagram :
//		{
//			// Code for when a diagram is selected
//			var theDiagram as EA.Diagram;
//			theDiagram = Repository.GetTreeSelectedObject();
//			
//			break;
//		}
//		case otAttribute :
//		{
//			// Code for when an attribute is selected
//			var theAttribute as EA.Attribute;
//			theAttribute = Repository.GetTreeSelectedObject();
//			
//			break;
//		}
//		case otMethod :
//		{
//			// Code for when a method is selected
//			var theMethod as EA.Method;
//			theMethod = Repository.GetTreeSelectedObject();
//			
//			break;
//		}
		default:
		{
			// Error message
			Session.Prompt( "This script does not support items of this type.", promptOK );
		}
	}
}

OnProjectBrowserScript();

/* ******************************************* FUNCTIONS ********************************************* */


/// OUD ///
function MaakEnumeratieVanElement( element /*: EA.Element*/ ) /* : Enumeration */
{
	var enumEl as EA.Element;
	enumEl = element;

	VT(t, "##### Enumeratie ##### ", enumEl.Name);

	var enumeratie = new Enumeration(enumEl.Name);
	for (k = 0, nrOfItems = enumEl.Attributes.Count; k < nrOfItems; ++k)
	// doorloop de items van de enum
	// en voeg aan enumeratie toe
	{
		var att as EA.Attribute;
		att = enumEl.Attributes.GetAt(k);

		//VT(t, "enum.Name", att.Name);
		//VT(t, "enum.Notes", att.Notes);
		//VT(t, "enum.Type", att.Type);

		var item = enumeratie.addItem(att.Name, att.Notes);
	}

	// test
	if (t)
	{
		enumeratie.printAll(5)
	};

	return enumeratie;
}


function MaakSchemaUitElement(openApiSpecification, el, enumeraties, enumReps)
// maakt een schema uit een EA element, indien mogelijk
// mogelijk als:
// <<representatie>>
//
{
	TT(t, "------------------------------------");
	TT(t, "--------MaakSchemaUitElement--------");
	TT(t, "------------------------------------");
	TT(t, "------------"+el.Name+"-------------");
	TT(t, "------------"+el.ElementID+"-------------");
	TT(t, "------------------------------------");
	if (enumeraties != null)
	{
		VT(t, "aantal enumeraties", enumeraties.enumerations.length);
	}
	VT(t, "el.Type", el.Type);
	VT(t, "el.Name", el.Name);
	VT(t, "el.Notes", el.Notes);
	VT(t, "el.Stereotype", el.Stereotype);
	VT(t, "el.ElementID", el.ElementID);
	VT(t, "el.ClassifierName", el.ClassifierName);
if (el.Type == "Class" && el.Stereotype == "representatie"){
		TT(t, "klasse en representatie, dus SCHEMA van maken");

		var schemaDescription;
		if (el.Notes != null && el.Notes != "")
		{
			schemaDescription = el.Notes;
		}
		else
		{
			schemaDescription = "#TODO";
		}

		// maak een nieuw schema object
		var schema = new Schema(el.Name, schemaDescription);
		//test
		//if(t){schema.printAll()};

		// TODO: overerving en dan allOf doen + - properties:, anders alleen properties:

		// TODO: attributen als properties. Extra tab als er overerving is.
		// TODO: per attribuut:
		// TODO:	- enumeratie via de dependency (let op: linked to element feature zichtbaar?)
		// TODO:	- enumeraties uit andere packages (nog niet zo belangrijk, ze staan er altijd naast tot nu toe)

		VT(t, "el.Attributes.Count", el.Attributes.Count); 

		for (j = 0, nrOfAttributes = el.Attributes.Count; j < nrOfAttributes; ++j)
		// doorloop de attributen van het element
		// en maak van elk een property
		{
			var att as EA.Attribute;
			att = el.Attributes.GetAt(j);

			TT(t,"***********ATTRIBUUT*************");
			VT(t, "att.Name", att.Name);
			VT(t, "att.LowerBound", att.LowerBound);
			VT(t, "att.UpperBound", att.UpperBound);
			VT(t, "att.Notes", att.Notes);
			VT(t, "att.Type", att.Type);
			VT(t, "att.AttributeGUID", att.AttributeGUID); // t.b.v. enumReps.getByLinkingAttributeGUID	

			// maak nieuw property object
			var property = new Property (att.Name);

			// required als lowerbound > 0
			if (parseInt(att.LowerBound) > 0)
			{
				property.required = true;
				TT(t,"required");
			}
			if (att.UpperBound == "*" || parseInt(att.UpperBound)>1)
			{
				property.isArray = true;
				TT(t,"is een array");
				// TODO: hoe dit weer te geven....?
			}

			VT(t,"att.Notes", att.Name);

			// description
			property.setDescription(att.Notes);
			// format uit type peuteren of uit tagged value (replace [ ?)
			ZetTypeEnFormat (property, att);		

			/*
				Zoek of de attribuut beperkt wordt door een enumeratie.

				Twee opties:
				1. enumeratie zit in de LinkedAttributes van de EnumerationRepresentations enumReps
				2. in de tagged value van het attribuut staat YAML::enumeratienaam  met de naam van de enumeratie

				1 prevaleert boven 2
			*/

			var enumRep;
			/*
				1. enumeratie zit in de LinkedAttributes van de EnumerationRepresentations enumReps
			*/
			enumRep = enumReps.getByLinkingAttributeGUID (att.AttributeGUID);

			if (enumRep == null)
			{
				/*
					2. in de tagged value van het attribuut staat YAML::enumeratienaam  met de naam van de enumeratie
				*/
				var atc = new AttributeTagCollection(att.TaggedValues);
				var enumtag as EA.AttributeTag;
				//enumtag = null;

				enumtag = atc.FindAttributeTagByName ("YAML::enumeratienaam");
				if (enumtag != null)
				{
					VT (t, "enumtag.Name", enumtag.Name);
					VT (t, "enumtag.Value", enumtag.Value);

					// zoek in verzameling enumeraties
					///OLD/// enumeratie = enumeraties.getByName(enumtag.Value);
					enumRep = enumReps.getByName(enumtag.Value);
				}
			}

			if (enumRep != null)
			{
				var enumeration = enumRep.Enumeration;
				VT (t, "gevonden enumeration.naam", enumeration.name);
				VT (t, "gevonden enumeration.items.length", enumeration.items.length);
				VT (t, "gevonden enumeration.aantalItems", enumeration.aantalItems());
				enumeration.printAll();

				// voeg het enumeratie object aan de property toe
				property.enumeration = enumeration;
				property.format = null; 	
			}


			// voeg property toe aan schema
			schema.addProperty (property);
		}

		/* ********
		 * DOORLOOP DE RELATIES
		 */
		// TODO: relaties uit als property herkennen met ref!
		// TODO:	- referentie? + de referentie-file ontcijferen uit het package waar de klasse in zit (niet gemakkelijk)

		VT(t, "el.Connectors.Count", el.Connectors.Count); 

		for (j = 0, nrOfConnectors = el.Connectors.Count; j < nrOfConnectors; ++j)
		// doorloop de Connectors van het element
		// en maak indien nodig van elk een property met een Reference
		{
			var con as EA.Connector;
			con = el.Connectors.GetAt(j);

			TT(t,"***********Connectors*************");
			VT(t, "con.Name", con.Name);
			VT(t, "con.Type", con.Type);
			VT(t, "con.Direction", con.Direction);
			VT(t, "con.Name", con.Name);
			VT(t, "con.Notes", con.Notes);
			VT(t, "con.ClientID", con.ClientID);
			VT(t, "con.ClientEnd.Role", con.ClientEnd.Role);
			VT(t, "ClientID).Name", Repository.GetElementByID(con.ClientID).Name);
			VT(t, "con SupplierID", con.SupplierID);
			VT(t, "con.SupplierEnd.Role", con.SupplierEnd.Role);
			VT(t, "SupplierID).Name", Repository.GetElementByID(con.SupplierID).Name);
			VT(t, "con StyleEx", con.StyleEx);

			// maak alvast een nieuw property object
			// misschien wordt ie niet gebruikt...
			var property = new Property (); // NB: name is nog niet bekend

			switch (true)
			{
				// Aggregatie
				case (con.Type == 'Aggregation' && con.SupplierID == el.ElementID):
					TT(t, "aggregatie en de supplier = hier moet een <"+ con.ClientEnd.Role +"> property komen");
					VT(t, "con.ClientEnd.Cardinality", con.ClientEnd.Cardinality);
					VT(t, "con.ClientEnd.RoleNote", con.ClientEnd.RoleNote);

					// bepaal property naam
					if (con.ClientEnd.Role != "")
					{
						property.name = con.ClientEnd.Role;
					}
					else
					{
						// TODO clientEnd.role leeg -> de client-klassenaam camelcasen
						property.name = CamelCase(Repository.GetElementByID(con.ClientID).Name);
					}

					// bepaal property description
					if (con.ClientEnd.RoleNote != "")
					{
						property.description = con.ClientEnd.RoleNote;
					}

					// cardinaliteit lezen om te bepalen of array en required
					// bepaal of property required en/of een array is
					var card = SplitCardinality(con.ClientEnd.Cardinality);
					if (card != null){
						VT(t, "card.lowerBound", card.lowerBound);
						VT(t, "card.upperBound", card.upperBound);

						if (card.lowerBound != "0")
						{
							property.required = true;
							TT(t,"required");
						}

						if (card.upperBound == "*" || parseInt(card.upperBound)>1)
						{
							property.isArray = true;
							TT(t,"is een array");
						}
					}

					// zoek referentie
					property.reference = VindReferentieUitElement(openApiSpecification, Repository.GetElementByID(con.ClientID));

					// alleen als referentie gevonden: voeg de property toe
					if (property.reference != null)
					{
						VT(t, "referentie", property.reference.wholeReference() );
						schema.addProperty (property);
					}
					break;

				// Associatie
				case (con.Type == 'Association' && con.ClientID == el.ElementID):
					TT(t, "associatie en de client = hier moet een <"+ con.SupplierEnd.Role +"> property komen");
					VT(t, "con.SupplierEnd.Cardinality", con.SupplierEnd.Cardinality);

					if (con.SupplierEnd.Role != "")
					{
						property.name = con.SupplierEnd.Role;
					}
					else
					{
						// TODO SupplierEnd.role leeg -> de Supplier-klassenaam camelcasen
						property.name = CamelCase( Repository.GetElementByID(con.SupplierID).Name );
					}

					// bepaal property description
					if (con.SupplierEnd.RoleNote != "")
					{
						property.description = con.SupplierEnd.RoleNote;
					}

					// cardinaliteit lezen om te bepalen of array en required
					// bepaal of property required en/of een array is
					var card = SplitCardinality(con.SupplierEnd.Cardinality);
					if (card != null){
						VT(t, "card.lowerBound", card.lowerBound);
						VT(t, "card.upperBound", card.upperBound);

						if (card.lowerBound != "0")
						{
							property.required = true;
						}

						if (card.upperBound == "*" || parseInt(card.upperBound)>1)
						{
							property.isArray = true;
						}
					}


					// zoek referentie
					property.reference = VindReferentieUitElement(openApiSpecification, Repository.GetElementByID(con.SupplierID));

					// alleen als referentie gevonden: voeg de property toe
					if (property.reference != null)
					{
						VT(t, "referentie", property.reference.wholeReference() );
						schema.addProperty (property);
					}
					break;

				// Overerving
				case (con.Type == 'Generalization' && con.ClientID == el.ElementID):
					TT(t, "Overerving en de client = hier moet een <allOf: "+ Repository.GetElementByID(con.SupplierID).Name +"> komen");
					VT(t, "con.Stereotype", con.Stereotype);
					// TODO : checken van <<allOf>> of kan dat wel zonder?

					// zoek referentie en wijs aan parent toe
					schema.parent = VindReferentieUitElement(openApiSpecification, Repository.GetElementByID(con.SupplierID));
					break;

				// Dependency (enum?)
				case (con.Type == 'Dependency' && con.ClientID == el.ElementID):
					TT(t, "Dependency naar: "+ Repository.GetElementByID(con.SupplierID).Name +". --> doe niets.");
					break;				
			}
		}

		// klaar met dit element
		return schema;

	}
}


function VindReferentieUitElement(openApiSpecification, element)
{
	var el as EA.Element;
	el = element;

	if (el == null)	{ return null};

	VT(t, "el.PackageID", el.PackageID );

	// zoek package van element
	var map as EA.Package;
	map =  Repository.GetPackageByID(el.PackageID);	
	VT(t, "map Name", map.Name );
	VT(t, "map Alias", map.Alias );
	VT(t, "map Phase", map.Element.Phase );
	VT(t, "map relatieve filepad", map.Element.Genfile );

	// maak relatieve pad netjes
	var relPad =  map.Element.Genfile.replace( /\//gi, "\\");				
	if (relPad.substr(relPad.length - 1) != "\\")
	{
		relPad += "\\";
	}			

	var refschema = "";
	if (map.Alias != null && map.Alias != "")
	{
	if (map.Alias != openApiSpecification.name 
		|| map.Element.Phase != openApiSpecification.mainVersion
		|| relPad != openApiSpecification.relativeFilepath )
		{
			// i.i.g. een ander schema
			refschema = map.Alias;

			//check relPad: als die hetzelfde is, maak hem leeg
			if (relPad == openApiSpecification.relativeFilepath)
			{
				relPad = null;
			}
			else
			{	
				// maak pad ook echt relatief: ga terug naar de root vanuit de openApiSpecification
				VT(t, "relatieve pad van de referentie", relPad);
				VT(t, "openApiSpecification.relativeFilepath", openApiSpecification.relativeFilepath);
				for (var i = 0, nrOf = openApiSpecification.relativeFilepath.split("\\").length - 1; i < nrOf; ++i)
				{
					relPad = "../" + relPad;
				}
			}

		}
		else // TODO: ook versie checken
		{
			// zelfde schema
		}
	}

	var referentie = new Reference(refschema, map.Element.Phase, el.Name, relPad);

	VT(t, "referentie", referentie.wholeReference() );

	return referentie;
}


// zoekt linked attribuut uit connector
function ZoekLinkedAttribuut (connector)
{	
	if (connector == null){return null};
	//var conn as EA.Connector;
	//conn = connector;
	var stijlen = connector.StyleEx;

	return attribuutNaam;
}

function ZetTypeEnFormat (property, att)
// converteert att.Type of tagged value naar een type en een format
// zet de type waarde en format van het attribuut
{
	// TODO
	// als [BLA] of {BLA} in type -> format = bla
	// als TaggedValue YAML::format een waarde heeft: format = bla
	// als die ongelijk zijn: geef melding in script en/of foutmelding-opmerking in YAML output


	//vind [format] in att.Type
	re = /\[(.*?)\]/g;

	var typeformat;

	//probeer format uit type te halen
	typeformat= re.exec(att.Type);
	VT(t, "[format]", typeformat);

	if (t && typeformat != null){
		VT(t, "[format][1]", typeformat[1]);
	}



	//probeer format uit Tag te halen	
	var atc = new AttributeTagCollection(att.TaggedValues);
	var formattag as EA.AttributeTag;
	formattag = atc.FindAttributeTagByName ("YAML::format");

	if (formattag != null)
	{
		VT(t, "YAML::format tag", formattag.Value);
	}

	// kies welk format te gebruiken + welke type
	if (typeformat != null)
	{
		TT(t, "typeformat not null: gebruik deze");
		// haal type van voor het [format] ding uit het att.Type veld
		typeRE = /(.*?)\s*\[/g;
		type = typeRE.exec(att.Type);
		VT(t, "type zonder [format]", "<"+type[1]+">");
		// zet type en format
		property.type = type[1];
		property.format = typeformat[1];

		if (formattag != null && formattag.Value != typeformat[1])
		// maar de tag is er ook en verschilt van de [format]
		{
			property.foutmelding = "Tagged value ("+formattag.Value+") verschilt van ["+typeformat[1]+"] in het UML attribuut!";
			Session.Prompt("Fout in attribuut: " + property.foutmelding, promptOK);
			TT(t, "Fout in attribuut: " + property.foutmelding); 
		}

	}
	else if (formattag != null)
	// geen format in attribuut-type maar wel in TaggedValue
	{	
		property.type = att.Type;
		property.format = formattag.Value;
	}
	else
	// geen format tag
	// neem het attribuuttype gewoon over
	{
		property.type = att.Type;
	}

	//voor de zekerheid, maar is niet nodig
	return property;
}

/* splitst een 0, 1, 0..1, 0..*, 1..* enz. string in een Cardinality object met een lowerBound en een upperBound
 * tenzij het formaat van de cardinalityString niet klopt of de string leeg is
 * card. -> lo - up
 * 0     -> 0 - 0
 * 1     -> 1 - 1
 * *     -> 0 - *
 * 0..1  -> 0 - 1
 * 0..*  -> 0 - *
 * 1..*  -> 1 - *
 */
function SplitCardinality (cardinalityString)
{
	var cardinality;

	//probeer lowerBound en upperBound uit cardinalityString te halen
	re = /(\d)?(\.\.)?([0-9\*])/g;
	var cardparts = re.exec(cardinalityString);

	if (cardparts != null){		
		for (i = 0, nrOf = cardparts.length; i < nrOf; ++i)
		{
			VT(t, i, cardparts[i]);
		}

		cardinality = new Cardinality(cardparts[1], cardparts[3]);
		if (cardinality.lowerBound == null || cardinality.lowerBound == "")
		{
			if (cardinality.upperBound == "*")
			{
				cardinality.lowerBound = 0;
			}
			else
			{
				cardinality.lowerBound = cardinality.upperBound;
			}
		}
	}
	else
	{
		TT(t, "geen resultaat");
	}
	return cardinality;
}


function Cardinality(lowerBound, upperBound)
{
	this.lowerBound = lowerBound;
	this.upperBound = upperBound;	
}

